import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Col, Input } from 'reactstrap';

class ActionLogin extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  render() {
    return (
      <div>
        <Button color="danger" onClick={this.toggle}>{this.props.buttonLabel}</Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Login To Website</ModalHeader>
          <ModalBody>
            <div style={{margin: '2rem 0rem 0rem 2rem'}}>
                <Form sm={6}>
                    <FormGroup row>
                    <Label for="phone" sm={2}>Phone</Label>
                    <Col sm={10}>
                        <Input type="text" name="phone" id="phone" placeholder="Phone" />
                    </Col>
                    </FormGroup>
                    <FormGroup row>
                    <Label for="password" sm={2}>Password</Label>
                    <Col sm={10}>
                        <Input type="password" name="password" id="password" placeholder="Password" />
                    </Col>
                    </FormGroup>
                    <FormGroup row>
                    <Col sm={{ size: 15, offset: 4 }}>
                        <FormGroup check>
                        <Label check>
                            <Input type="checkbox" id="checkbox2" />{' '}
                            Remember Me
                        </Label>
                        </FormGroup>
                    </Col>
                    </FormGroup>
                    <FormGroup check row>
                    <Col sm={{ size: 11, offset: 4 }}>
                        <Button color="success">Submit</Button>
                    </Col>
                    </FormGroup>
                </Form>
                </div>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.toggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default ActionLogin;
